class Snowflake
{

    //idbit5bitworkerid5bitdatacenterid
    const int WorkerIdBits = 5;
    const int DatacenterIdBits = 5;
    //bit
    const int SequenceBits = 12;
    //
    const long MaxWorkerId = -1L ^ (-1L << WorkerIdBits);
    const long MaxDatacenterId = -1L ^ (-1L << DatacenterIdBits);
    private const long SequenceMask = -1L ^ (-1L << SequenceBits);
    //
    public const int WorkerIdShift = SequenceBits;
    public const int DatacenterIdShift = SequenceBits + WorkerIdBits;
    public const int TimestampLeftShift = SequenceBits + WorkerIdBits + DatacenterIdBits;

    //
    private long _sequence = 0L;
    //
    private long _lastTimestamp = -1L;


    public long WorkerId { get; protected set; }
    public long DatacenterId { get; protected set; }

    public Snowflake(long workerId, long datacenterId, long sequence = 0L) 
    {
        WorkerId = workerId;
        DatacenterId = datacenterId;
        _sequence = sequence;

        // sanity check for workerId
        if (workerId > MaxWorkerId || workerId < 0) 
        {
            throw new ArgumentException( String.Format("worker Id can't be greater than {0} or less than 0", MaxWorkerId) );
        }

        if (datacenterId > MaxDatacenterId || datacenterId < 0)
        {
            throw new ArgumentException( String.Format("datacenter Id can't be greater than {0} or less than 0", MaxDatacenterId));
        }

    }
    /// <summary>
    /// 
    /// </summary>
    /// <returns></returns>
    public long TimeGen()
    {
        DateTime Jan1st1970 = new DateTime(1970, 1, 1, 0, 0, 0, DateTimeKind.Utc);//
        return (long)(DateTime.UtcNow - Jan1st1970).TotalMilliseconds;
    }

    readonly object _lock = new Object();

    public virtual long NextId()
    {
        lock (_lock)
        {
            var timestamp = TimeGen();

            if (timestamp < _lastTimestamp)
            {
                throw new InvalidSystemClock(String.Format(
                    "{0}", _lastTimestamp - timestamp));
            }

            if (_lastTimestamp == timestamp)
            {
                _sequence = (_sequence + 1) & SequenceMask;
                if (_sequence == 0)
                {
                    //
                    timestamp = TilNextMillis(_lastTimestamp);
                }
            }
            else
            {
                _sequence = 0;
            }

            _lastTimestamp = timestamp;
            //snowflake
            var id = (timestamp << TimestampLeftShift) |
                     (DatacenterId << DatacenterIdShift) |
                     (WorkerId << WorkerIdShift) | _sequence;

            return id;
        }
    }
    /// <summary>
    /// 
    /// </summary>
    protected virtual long TilNextMillis(long lastTimestamp)
    {
        var timestamp = TimeGen();
        while (timestamp <= lastTimestamp)
        {
            //
            timestamp = TimeGen();
        }
        return timestamp;
    }
}
```

