'value' => 'title',
'headerOptions' => ['width' => '100'],
'contentOptions'=>['width'=>20]
],


'rowOptions' => function($model, $key, $index, $grid) {
return ['class' => $index % 2 ==0 ? 'label-red' : 'label-green'];
},


